const todoCentor = (function () {
  /**
   * @description: 获取初始todo
   * @returns {[{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }]}
   */
  function loadTodos() {
    return JSON.parse(localStorage.getItem("todos")) || []
  }

  /**
   * @description: 存储todos
   * @param {[{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }]} todos
   */
  function saveTodos(todos) {
    localStorage.setItem("todos", JSON.stringify(todos))
  }

  /**
   * @description: 改变todos中todo值
   * @param {[{
  *  id: string;
  *  name: string;
  *  done: boolean;
  * }]} todos
  * @param {{
  *  id: string;
  *  name: string;
  *  done: boolean;
  * }} todos todo
  */
  function addTodo(todos, todo) {
    todos.push(todo)
  }

  /**
   * @description: 
   * @param {[{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }]} todos
   * @param {string} todoID
   * @param {boolean} done
   * @return {*}
   */
  function updateTodoDone(todos, todoID, done) {
    todos.forEach(elem => {
      if (elem.id === todoID) {
        elem.done = done
      }
    })
  }

  /**
   * @description: 
   * @param {[{
  *  id: string;
  *  name: string;
  *  done: boolean;
  * }]} todos
  * @param {string} todoID
  * @param {string} name
  * @return {*}
  */
 function updateTodoName(todos, todoID, name) {
   todos.forEach(elem => {
     if (elem.id === todoID) {
       elem.name = name
     }
   })
 }

  /**
   * @description: 更新所有todo 的 done 状态
   * @param {[{
  *  id: string;
  *  name: string;
  *  done: boolean;
  * }]} todos
  * @param {boolean} done
  * @return {*}
  */
  function updateTodosDone(todos, done) {
    todos.forEach(elem => {
      if (elem.done !== done) {
        elem.done = done
      }
    })
  }

  /**
   * @description: 删除指定todo id
    * @param {[{
  *  id: string;
  *  name: string;
  *  done: boolean;
  * }]} todos
   * @param {string} id
   */
  function removeTodo(todos, id) {
    const index = todos.findIndex(todo => todo.id === id);

    if (index !== -1) {
      // 使用 splice() 方法删除找到的对象
      todos.splice(index, 1);
    }

    return todos
  }

  return {
    addTodo,
    loadTodos,
    saveTodos,
    updateTodoDone,
    updateTodoName,
    updateTodosDone,
    removeTodo,
  }
})()

// 控制 todo dom 元素
const elementCenter = (function () {

  const input = document.querySelector('.todo-header input')
  const list = document.querySelector('.todo-main')
  const allCheckBox = document.querySelector('.todo-footer input')
  const finishNumSpan = document.querySelector('#finish_num')
  const allNumSpan = document.querySelector('#all_num')

  // 按钮添加任务
  input.addEventListener('keyup', function (e) {
    if (e.key === 'Enter') {
      const value = this.value.trim()
      if (!value) return

      const todo = {
        id: `${Date.now()}`,
        name: value,
        done: false
      }

      addTodo(todo)
      eventCenter.publish('todo-add', todo)

      this.value = ''
    }
  })

  allCheckBox.addEventListener('change', function (e) {
    console.log('e.target:', e.target)
    console.log('e.target:', e.target.checked) // todo 子项会造成联动，需提前保存其状态
    checked = e.target.checked
    eventCenter.publish('update-todos-checked', checked)
  })

  /**
   * @description: 
   * @param {[{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }]} todos
   * @return {*}
   */
  function updateTodoInfos(todos) {

    // 更新底部所有
    const finishNum = todos.filter(todo => todo.done).length
    finishNumSpan.innerHTML = finishNum
    allNumSpan.innerHTML = todos.length
    if (finishNum == todos.length) {
      allCheckBox.checked = true
    }else{
      allCheckBox.checked = false
    }

    // 更新单个checked
    todos.forEach(todo => {
      const id = `change-${todo.id}`
      document.getElementById(id).checked = todo.done
    })
  }

  /**
   * @description: 添加todo元素
   * @param {{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }} todo
   * @return {*}
   */
  function addTodo(todo) {
    const li = document.createElement('li')
    const checked = todo.done ? 'checked' : ''
    li.id = todo.id

    li.innerHTML = `


        ${todo.name}


      删除
      编辑
    `

    list.appendChild(li)
  }

  /**
   * @description: 
   * @param {[{
   *  id: string;
   *  name: string;
   *  done: boolean;
   * }]} todos
   * @return {*}
   */
  function initTodos(todos) {
    todos.forEach(todo => {
      addTodo(todo)
    })
  }

  /**
   * @description: 
   * @param {string} todoID
   */
  function removeTodo(todoID) {
    const li = document.getElementById(todoID)
    li.parentNode.removeChild(li)
  }

  return {
    updateTodoInfos,
    initTodos,
    removeTodo,
  }
})();

/**
   * @description: 处理todo的change事件
   * @param {HTMLElement} target
   */
function handleTodoChecked(target) {
  console.log('target:', target)
  const todoIDs = target.id.split('-')
  const todoID = todoIDs[todoIDs.length - 1]
  const checked = target.checked

  eventCenter.publish('update-todo-checked', {
    todoID,
    checked
  })
}

/**
 * @description: 
