 * @return {*}
 */
function handleTodoDelete(target) {
  const todoIDs = target.id.split('-')
  const todoID = todoIDs[todoIDs.length - 1]

  elementCenter.removeTodo(todoID)
  eventCenter.publish('todo-delete', todoID)
}

/**
 * @description: 
