          get() {
            track() // 通知 vue 追踪数据变化
            return value;
          },
          set(newValue) {
            value = newValue;
            trigger() // 通知vue 重新解析模板
          },
        };
      });
    }

    return {
      keyWord: ref("hello"), //使用vue 内置ref
      keyWord2: myRef("hello")
    };
  },
};



